package bytematcher

import (
	"bytes"
	"testing"

	//"github.com/richardlehane/siegfried/internal/bytematcher/frames"
	"github.com/richardlehane/siegfried/internal/bytematcher/frames/tests"
	"github.com/richardlehane/siegfried/internal/siegreader"
	//"github.com/richardlehane/siegfried/pkg/config"
	"github.com/richardlehane/siegfried/pkg/core"
)

var testPartials = [][][2]int64{
	{{0, 3}, {0, 4}},
	{{1, 1}},
	{{2, 1}, {2, 3}, {2, 4}},
}

var expectPartials = [][][2]int64{
	{{0, 3}, {1, 1}, {2, 1}},
	{{0, 4}, {1, 1}, {2, 1}},
	{{0, 3}, {1, 1}, {2, 3}},
	{{0, 4}, {1, 1}, {2, 3}},
	{{0, 3}, {1, 1}, {2, 4}},
	{{0, 4}, {1, 1}, {2, 4}},
}

func TestIteratePartials(t *testing.T) {
	var i int
	next := iteratePartials(testPartials)
	for p := next(0, 2); p != nil; p = next(0, 2) {
		if i > 5 {
			t.Fatalf("looped too many times on partials: %d", i)
		}
		ex := expectPartials[i]
		for j, v := range ex {
			if v != p[j] {
				t.Fatalf("failed on %d iteration, wrong partial; expected %v and got %v", i+1, v, p[j])
			}
		}
		i++
	}
	if i != len(expectPartials) {
		t.Fatalf("expected %d partials, got: %d", len(expectPartials), i)
	}
}

func setup() (chan<- strike, <-chan core.Result) {
	m, _, _ := Add(nil, SignatureSet(tests.TestSignatures), nil)
	bm := m.(*Matcher)
	bufs := siegreader.New()
	buf, _ := bufs.Get(bytes.NewBuffer(TestSample1))
	buf.SizeNow()
	res := make(chan core.Result)
	return bm.scorer(buf, bm.priorities.WaitSet(), make(chan struct{}), res), res
}

func TestScorer(t *testing.T) {
	scorer, res := setup()
	scorer <- strike{0, 0, 0, 4, false, false}
	scorer <- strike{1, 0, 17, 9, true, false}
	scorer <- strike{1, 1, 30, 5, true, false}
	if r := <-res; r.Index() != 0 {
		t.Errorf("expecting result %d, got %d", 0, r.Index())
	}
}

// 2 Jan 17 BenchmarkScorer   	   20000	    111048 ns/op
func BenchmarkScorer(bench *testing.B) {
	for i := 0; i < bench.N; i++ {
		bench.StopTimer()
		scorer, res := setup()
		bench.StartTimer()
		scorer <- strike{0, 0, 0, 4, false, false}
		scorer <- strike{1, 0, 17, 9, true, false}
		scorer <- strike{1, 1, 30, 5, true, false}
		_ = <-res
	}
}

